#include "stdio.h"

#define MAX_LEN 1000

char arr[MAX_LEN]={
""};

int lenK(const char *arr, const int pos_start, int* result, const int series_len)
{
    int i;
    for(i=pos_start; i< (pos_start+series_len); i++)
    {
        if((arr[i] - 48) == 0)
        {
            *result = 1;
            return  lenK(arr, i+1, result, series_len);
        }
        *result *= (arr[i] - 48);
    }
    return pos_start;
}

int main(int argc, int argv[])
{
    int i;
    int greatest = 1;
    int pos;


    for(i=0; i< MAX_LEN; i++)
    {
        int result = 1;
        i = lenK(arr, i, &result, 5);
        if(result > greatest)
        {
            pos = i;
            greatest = result;
        }
    }

    printf("pos = %d : %d\n", pos, greatest);
    return 0;
}
